export function  modulo(n : number, m : number){
    return ( n >= 0)
        ? n % m
        : (m+(n%m))%m;
  }

export function  sign(n : number){
    return   (n > 0) ? 1
         : (n < 0) ? -1
         : 0;
  }

export function  binSign(n : number){
  return ( n >= 0)
         ?  1
         : -1;
  }

export function solveQuad(a,b,c){
    if (a === 0 && b !== 0) return [-c/b];
    let diskr = b * b - (4 * a * c)
	if (diskr < 0) return [];
    if (diskr == 0) return [-b];
	let w1 = (-b + Math.sqrt(diskr)) / (2 * a);
	let w2 = (-b - Math.sqrt(diskr)) / (2 * a);
    
    return [w1, w2].sort(function(a,b){return a-b});
}

